import { useSelector } from "react-redux"
import { Product } from "../Product"
import "./styles.css"

export const Cart = () => {

    const cart = useSelector(state => state.cart)

    const teste = cart.reduce((acc, item) => acc + item.price, 0)

    return (
        <div className="container-items">
            <div>
                {cart.length === 0 ? <h3>Carrinho vazio...</h3> 
                : 
                <h1>Carrinho de compras</h1>
                }
               {cart.map(product =>  {
                   return (
                       <Product key={product.id} product={product} isRemovable />
                   )
               }  )}
            </div>

            <div className="total">
                <h4>Total: R${teste}</h4>
                <h5>{cart.length} Produtos</h5>
            </div>
        </div>
    )
}
