import { useSelector } from "react-redux"
import { ShowCart } from "../CartProducts"
import "./styles.css"

export const Cart = () => {

    const cart = useSelector(state => state.cart)

    const teste = cart.reduce((acc, item) => acc + item.price, 0).toFixed(2)

    return (
        <div className="container-items">
            <div className="container-flex">
                {cart.length === 0 && <h3>Carrinho vazio...</h3> 

                }
               {cart.map(product =>  {
                   return (
                    <ShowCart key={product.id} product={product} isRemovable/>
                   )
               }  )}
            </div>

            <div className="total">
                <h2>Resumo do pedido</h2>
                <h5>{cart.length} Produtos</h5><span>Total: R${teste}</span>
            </div>
        </div>
    )
}
