import { Button } from "@material-ui/core"
import { useSelector } from "react-redux"
import { useHistory } from "react-router-dom"
import { ShowCart } from "../CartProducts"
import "./styles.css"

export const Cart = () => {

    const history = useHistory()

    const cart = useSelector(state => state.cart)

    const teste = cart.reduce((acc, item) => acc + item.price, 0).toFixed(2)

    return (
        <div className="container-items">
            <div className="container-flex">
                {cart.length === 0 && <div><h2>Carrinho vazio...</h2><Button onClick={() => history.push("/")} variant="contained">Ir as compras</Button></div>

                }
               {cart.map(product =>  {
                   return (
                    <ShowCart key={product.id} product={product} isRemovable/>
                   )
               }  )}
            </div>

            <div className="total">
                <h3>Resumo do pedido</h3>
                <h5>{cart.length} Produtos</h5><span>Total: R${teste}</span>
                <Button variant="contained" color="secondary">Finalizar pedido</Button>
            </div>
        </div>
    )
}
